<?php function login($perms){if(isset($_SESSION["login_currentPerms"])and $_SESSION["login_currentPerms"]==$perms){return true;}else{require_once("session.php");$_SESSION["login_recPerms"]=$perms;$_SESSION["login_path"]=$_SERVER['HTTP_HOST'].$_SERVER['PHP_SELF'];require_once("frontEndLogin.php");return false;}} ?>